def fcs_crc16(data_stream):
    # http://technofart.blogspot.de/2012/04/working-aprs-afsk-generation-code.html

    fcs = 0xffff

    for bit in data_stream:
        shiftBit = fcs & 0x01

        fcs = fcs >> 1
        if str(shiftBit) != bit:
            fcs = fcs ^ 0x8408

    fcs = fcs ^ 0xffff

    return bin(fcs)[2:].zfill(16)[::-1]  # Send FCS low-byte and LSB first


